import React, { Component } from "react";
import $ from "jquery";
import Card from "../components/card";

class Gallery extends Component {
    constructor() {
        super();
        this.state = {
            buku: [
                {
                    isbn: "12345",
                    judul: "Bulan",
                    penulis: "Tere Liye",
                    penerbit: "CV Harapan Kita",
                    harga: 90000,
                    cover: "https://drive.google.com/uc?id=1ui-jyKgu3DqFyo7VKJu-FFXkaNQN3aSg",
                },
                {
                    isbn: "12346",
                    judul: "Anak Badai",
                    penulis: "Tere Liye",
                    penerbit: "CV Nusa Bangsa",
                    harga: 80000,
                    cover: "https://drive.google.com/uc?id=1rJDcCOmsd14NL6DG3Wps_kewZomGcLU-",
                },
                {
                    isbn: "54321",
                    judul: "Bumi",
                    penulis: "Tere Liye",
                    penerbit: "CV Nusa Bangsa",
                    harga: 70000,
                    cover: "https://drive.google.com/uc?id=1e-thvq7lkG1_gw0FqHzRoiAhfhdgpOUj",
                },
            ],
            action: "",
            isbn: "",
            judul: "",
            penulis: "",
            penerbit: "",
            harga: 0,
            cover: "",
            selectedItem: null,
            keyword: "",
            filterBuku: []
        };
        this.state.filterBuku = this.state.buku;
    }

    Add = () => {
        // Menampilkan komponen modal
        $("#modal_buku").modal("show");
        this.setState({
            isbn: Math.random(1, 10000000),
            judul: "",
            penulis: "",
            penerbit: "",
            cover: "",
            harga: 0,
            action: "insert",
            selectedItem: null,
        });
    };

    Edit = (item) => {
        // menampilkan komponen modal
        $("#modal_buku").modal("show")
        this.setState({
            isbn: item.isbn,
            judul: item.judul,
            penulis: item.penulis,
            penerbit: item.penerbit,
            cover: item.cover,
            harga: item.harga,
            action: "update",
            selectedItem: item
        })
    }

    Save = (event) => {
        event.preventDefault();
        // menampung data state buku
        let tempBuku = this.state.buku


        if (this.state.action === "insert") {
            // menambah data baru
            tempBuku.push({
                isbn: this.state.isbn,
                judul: this.state.judul,
                penulis: this.state.penulis,
                penerbit: this.state.penerbit,
                cover: this.state.cover,
                harga: this.state.harga,
            })
        } else if (this.state.action === "update") {
            // menyimpan perubahan data
            let index = tempBuku.indexOf(this.state.selectedItem)
            tempBuku[index].isbn = this.state.isbn
            tempBuku[index].judul = this.state.judul
            tempBuku[index].penulis = this.state.penulis
            tempBuku[index].penerbit = this.state.penerbit
            tempBuku[index].cover = this.state.cover
            tempBuku[index].harga = this.state.harga
        }


        this.setState({ buku: tempBuku })

        // menutup komponen modal_buku
        $("#modal_buku").modal("hide")
    }

    Drop = (item) => {
        // beri konfirmasi untuk menghapus data
        if (window.confirm("Apakah anda yakin ingin menghapus data ini?")) {
            // menghapus data
            let tempBuku = this.state.buku
            // posisi index data yg akan dihapus
            let index = tempBuku.indexOf(item)


            // hapus data
            tempBuku.splice(index, 1)


            this.setState({ buku: tempBuku })
        }
    }

    searching = () => {
        let keyword = this.state.keyword.toLowerCase();
        let tempBuku = this.state.buku;
        let result = tempBuku.filter(item => {
            return (
                item.judul.toLowerCase().includes(keyword) ||
                item.penulis.toLowerCase().includes(keyword) ||
                item.penerbit.toLowerCase().includes(keyword)
            );
        });

        this.setState({ filterBuku: result });
    };


    render() {
        return (
            <div className="container">
                <div className="row">
                {this.state.filterBuku.map((item, index) => (
    <Card
        judul={item.judul}
        penulis={item.penulis}
        penerbit={item.penerbit}
        harga={item.harga}
        cover={item.cover}
        onEdit={() => this.Edit(item)}
        onDrop={() => this.Drop(item)}
        key={index}
    />
))}

                </div>

                <button className="btn btn-success" onClick={() => this.Add()}>
                    Tambah Data
                </button>

                {/* Komponen modal sebagai kontrol manipulasi data */}
                <div className="modal" id="modal_buku">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            {/* Modal header */}
                            <div className="modal-header">Form Buku</div>

                            {/* Modal body */}
                            <div className="modal-body">
                                <form onSubmit={(ev) => this.Save(ev)}>
                                    Judul Buku
                                    <input
                                        type="text"
                                        className="form-control mb-2"
                                        value={this.state.keyword}
                                        onChange={(ev) => this.setState({ keyword: ev.target.value })}
                                        onKeyDown={this.searching}
                                        required
                                    />



                                    {/* ... (Form input dan elemen lainnya) */}

                                    <button className="btn btn-info btn-block" type="submit">
                                        Simpan
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Gallery;
