import React, { Component } from "react";
import $ from "jquery";
import Card from "../components/card";

class Gallery extends Component {
    constructor() {
        super();
        this.state = {
            buku: [
                {
                    isbn: "12345",
                    judul: "Bulan",
                    penulis: "Tere Liye",
                    penerbit: "CV Harapan Kita",
                    harga: 90000,
                    cover: "https://drive.google.com/uc?id=1ui-jyKgu3DqFyo7VKJu-FFXkaNQN3aSg",
                },
                {
                    isbn: "12346",
                    judul: "Anak Badai",
                    penulis: "Tere Liye",
                    penerbit: "CV Nusa Bangsa",
                    harga: 80000,
                    cover: "https://drive.google.com/uc?id=1rJDcCOmsd14NL6DG3Wps_kewZomGcLU-",
                },
                {
                    isbn: "54321",
                    judul: "Bumi",
                    penulis: "Tere Liye",
                    penerbit: "CV Nusa Bangsa",
                    harga: 70000,
                    cover: "https://drive.google.com/uc?id=1e-thvq7lkG1_gw0FqHzRoiAhfhdgpOUj",
                },
            ],
            action: "",
            isbn: "",
            judul: "",
            penulis: "",
            penerbit: "",
            harga: 0,
            cover: "",
            selectedItem: null,
            keyword: "",        
            filterBuku: [],
        };
    }

    componentDidMount() {
        // Fetch data or set initial state here
        // Example: fetch("your_api_endpoint").then(response => response.json()).then(data => this.setState({ buku: data }));
    }

    Add = () => {
        // Menampilkan komponen modal
        $("#modal_buku").modal("show");
        this.setState((prevState) => ({
            isbn: Math.floor(Math.random() * 10000000) + 1,
            judul: "",
            penulis: "",
            penerbit: "",
            cover: "",
            harga: 0,
            action: "insert",
            selectedItem: null,
        }));
    };

    Edit = (item) => {
        // Menampilkan komponen modal
        $("#modal_buku").modal("show");
        this.setState({
            isbn: item.isbn,
            judul: item.judul,
            penulis: item.penulis,
            penerbit: item.penerbit,
            cover: item.cover,
            harga: item.harga,
            action: "update",
            selectedItem: item,
        });
    };

    Save = (event) => {
        event.preventDefault();
        let tempBuku = [...this.state.buku];

        if (this.state.action === "insert") {
            tempBuku.push({
                isbn: this.state.isbn,
                judul: this.state.judul,
                penulis: this.state.penulis,
                penerbit: this.state.penerbit,
                cover: this.state.cover,
                harga: this.state.harga,
            });
        } else if (this.state.action === "update") {
            let index = tempBuku.indexOf(this.state.selectedItem);
            tempBuku[index] = {
                ...tempBuku[index],
                isbn: this.state.isbn,
                judul: this.state.judul,
                penulis: this.state.penulis,
                penerbit: this.state.penerbit,
                cover: this.state.cover,
                harga: this.state.harga,
            };
        }

        this.setState({ buku: tempBuku });

        $("#modal_buku").modal("hide");
    };

    Drop = (item) => {
        if (window.confirm("Apakah anda yakin ingin menghapus data ini?")) {
            let tempBuku = [...this.state.buku];
            let index = tempBuku.indexOf(item);
            tempBuku.splice(index, 1);
            this.setState({ buku: tempBuku });
        }
    };

    render() {
        return (
            <div className="container">
                <div className="row">
                    {this.state.buku.map((item, index) => (
                        <Card
                            judul={item.judul}
                            penulis={item.penulis}
                            penerbit={item.penerbit}
                            harga={item.harga}
                            cover={item.cover}
                            onEdit={() => this.Edit(item)}
                            onDrop={() => this.Drop(item)}
                            key={index}
                        />
                    ))}
                </div>

                <button className="btn btn-success" onClick={() => this.Add()}>
                    Tambah Data
                </button>

                <div className="modal" id="modal_buku">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">Form Buku</div>

                            <div className="modal-body">
                                <form onSubmit={(ev) => this.Save(ev)}>
                                    Judul Buku
                                    <input
                                        type="text"
                                        className="form-control mb-2"
                                        value={this.state.judul}
                                        onChange={(ev) => this.setState({ judul: ev.target.value })}
                                        required
                                    />

// ... (Kode sebelumnya)

{/* Add other form fields for author, publisher, price, and cover image */}
Penulis
<input
    type="text"
    className="form-control mb-2"
    value={this.state.penulis}
    onChange={(ev) => this.setState({ penulis: ev.target.value })}
    required
/>

Penerbit
<input
    type="text"
    className="form-control mb-2"
    value={this.state.penerbit}
    onChange={(ev) => this.setState({ penerbit: ev.target.value })}
    required
/>

Harga
<input
    type="number"
    className="form-control mb-2"
    value={this.state.harga}
    onChange={(ev) => this.setState({ harga: ev.target.value })}
    required
/>

Cover
<input
    type="text"
    className="form-control mb-2"
    value={this.state.cover}
    onChange={(ev) => this.setState({ cover: ev.target.value })}
    required
/>

<button className="btn btn-info btn-block" type="submit">
    Simpan
</button>
</form>
</div>
</div>
</div>
</div>
</div>
);
}
}

export default Gallery;
