import React, { Component } from "react";
import $ from "jquery";
import Card from "../components/card";

class Gallery extends Component {
    constructor() {
        super();
        this.state = {
            buku: [
                {
                    isbn: "9786232423824",
                    judul: "A Happy Life",
                    penulis: "Lucius Annaeus Seneca",
                    penerbit: "Noura Books",
                    harga: 90000,
                    cover: "A Happy Life",
                }
                
            ],
            action: "",
            isbn: "",
            judul: "",
            penulis: "",
            penerbit: "",
            harga: 0,
            cover: "",
            selectedItem: null,
            keyword: "",
            filterBuku: [],
        };
    }

    Add = () => {
        $("#modal_buku").modal("show");
        this.setState({
            isbn: Math.random(1, 10000000),
            judul: "",
            penulis: "",
            penerbit: "",
            cover: "",
            harga: 0,
            action: "insert",
            selectedItem: null,
        });
    };

    Edit = (item) => {
        $("#modal_buku").modal("show");
        this.setState({
            isbn: item.isbn,
            judul: item.judul,
            penulis: item.penulis,
            penerbit: item.penerbit,
            cover: item.cover,
            harga: item.harga,
            action: "update",
            selectedItem: item,
        });
    };

    Save = (event) => {
        event.preventDefault();
        let tempBuku = this.state.buku;

        if (this.state.action === "insert") {
            tempBuku.push({
                isbn: this.state.isbn,
                judul: this.state.judul,
                penulis: this.state.penulis,
                penerbit: this.state.penerbit,
                cover: this.state.cover,
                harga: this.state.harga,
            });
        } else if (this.state.action === "update") {
            let index = tempBuku.indexOf(this.state.selectedItem);
            tempBuku[index] = {
                ...tempBuku[index],
                isbn: this.state.isbn,
                judul: this.state.judul,
                penulis: this.state.penulis,
                penerbit: this.state.penerbit,
                cover: this.state.cover,
                harga: this.state.harga,
            };
        }

        this.setState({ buku: tempBuku });
        $("#modal_buku").modal("hide");
    };

    Drop = (item) => {
        if (window.confirm("Apakah anda yakin ingin menghapus data ini?")) {
            let tempBuku = this.state.buku;
            let index = tempBuku.indexOf(item);
            tempBuku.splice(index, 1);
            this.setState({ buku: tempBuku });
        }
    };

    handleSearch = (event) => {
        const keyword = event.target.value.toLowerCase();
        this.setState({ keyword }, () => {
            this.filterBooks();
        });
    };

    filterBooks = () => {
        const { buku, keyword } = this.state;
        const filterBuku = buku.filter(
            (item) =>
                item.judul.toLowerCase().includes(keyword) ||
                item.penulis.toLowerCase().includes(keyword) ||
                item.penerbit.toLowerCase().includes(keyword)
        );
        this.setState({ filterBuku });
    };

    render() {
        return (
            <div className="container">
                <input
                    type="text"
                    className="form-control mb-2"
                    placeholder="Cari buku..."
                    onChange={this.handleSearch}
                />

                <div className="row">
                    {this.state.filterBuku.length > 0 ? (
                        this.state.filterBuku.map((item, index) => (
                            <Card
                                judul={item.judul}
                                penulis={item.penulis}
                                penerbit={item.penerbit}
                                harga={item.harga}
                                cover={item.cover}
                                onEdit={() => this.Edit(item)}
                                onDrop={() => this.Drop(item)}
                                key={index}
                            />
                        ))
                    ) : (
                        <p>Tidak ada buku yang sesuai dengan pencarian.</p>
                    )}
                </div>

                <button className="btn btn-success" onClick={() => this.Add()}>
                    Tambah Data
                </button>

                <div className="modal" id="modal_buku">
                    {/* ... (your existing modal code) */}
                </div>
            </div>
        );
    }
}

export default Gallery;
